function subFolders = getListOfDirs (path)# usage getListOfDirs(path)#   path : a string containing the parent directory path# returns a list of child directories# to access the name     subFolders = [];  % Get a list of all files and folders in this folder.  files = dir(path);  % Get a logical vector that tells which is a directory.  dirFlags = [files.isdir]  % Extract only those that are directories.  subFolders = files(dirFlags)  % Exclude "." and ".." directories  subFolders(1:2) = []endfunction